menu.script:

function on_menu_item(index)
	if index == 0 then
		Dialog.get("singlerplayer"):setVisible(true);
	elseif index == 1 then
		Dialog.get("server"):setVisible(true);
	elseif index == 2 then
		Dialog.get("join"):setVisible(true);
	elseif index == 3 then
		Dialog.get("settings"):setVisible(true);
	elseif index == 4 then
		Dialog.get("profile"):setVisible(true);
	elseif index == 5 then
		Engine.get():stop()
	end
end
