menu.script:

function on_menu_item(index)
	if index == 0 then
		menu.show_dialog("singleplayer");
	elseif index == 1 then
		menu.show_dialog("server");
	elseif index == 2 then
		menu.show_dialog("join");
	elseif index == 3 then
		menu.start_editor();
	elseif index == 4 then
		menu.show_dialog("settings");
	elseif index == 5 then
		menu.show_dialog("profile");
	elseif index == 6 then
		core.exit();
	end
end
